.collapsible-list ul li ul{display:none}.body{font-family:Noto Sans,sans-serif}.mw-body-content{font-size:0.950em}.cdx-menu-item[title*="/"]:not([title$="/"]){display:none !important}#mw-content-text{margin-top:0 !important}.docnav{background-color:var(--background-color-neutral,#eaecf0);border:1px solid var(--border-color-base,#a2a9b1);padding:2px 10px;margin:10px 0;line-height:1;clear:both;overflow:hidden}.docnav > div:first-child{margin:4px 0;float:left}.docnav > div:nth-child(2){margin:4px 0;float:right}.docnav > div:last-child{text-align:center}.docnav > div:last-child > div{margin:4px 0}.docnav img{vertical-align:middle}.warning-box{text-align:center;background:var(--background-color-warning-subtle,#FDE0A8);border:2px solid var(--border-color-warning,#FF5706);margin-block:1em;margin-inline:clamp(1em,6vw,7em);padding-block:0.5em;padding-inline:clamp(1em,5vw,2em);word-break:normal;overflow-wrap:break-word}table.fcinfobox > tbody{display:table !important;width:-moz-available !important;width:-webkit-fill-available !important}li.approved-revision{border-color:var(--border-color-success,#099979) !important}.approved-revision{background-color:var(--background-color-success-subtle,#dff2eb) !important}@media print{body{background:#fff !important;font-size:20px}h2,.mediawiki h2{font-size:30px}.note{background:#eee !important}p,ul li{font-size:20px}#footer{font-size:0.7em}#footer li,#footer li a{color:#000 !important}}